/*jshint esversion: 6 */

import React from 'react';
import { requireNativeComponent } from 'react-native';

type AlanButtonProps = {
    params: array,
}

const AlanButton = (props: AlanButtonProps) => {
    return (
        <RNTAlanButton {...props} />
    )
}

var RNTAlanButton = requireNativeComponent('RNTAlanButton', AlanButton);

export type AlanViewProps = {
    projectid: string,
    host: string,
    authData: object,
}

export const AlanView = (props: AlanViewProps) => {
    return (
        <AlanButton params={[props.projectid, props.host, props.authData, '1.18.0']}/>
    )
}
